mapper.select:
select
from voc_management_code where top_comn_cd = #{topComnCd}
and comn_cd = #{comnCd}
and comn_cd in
#{item}
and prnts_cd is not null

mapper.delete:
delete from voc_dir_org_mapping
where
    dir_cd = #{dirCd}
    and

        org_id in

            #{org.orgId}

update.foreach:
update voc_dir_org_mapping
set
    primary_org_yn = #{org.primaryOrgYn}
where
    dir_cd = #{dirCd}
    and
    org_id = #{org.orgId}
