mapper.update:
UPDATE TB_SYS_FILE_BAS
   SET FILE_NM             =         #{fileNm}
     , FILE_SIZE           =         #{fileSize}
     , FILE_SAVE_NM        =         #{fileSaveNm}
     , FILE_URL            =         #{fileUrl}
     , FILE_EXT_NM         =         #{fileExtNm}
     , AMDR_ID             =         #{amdrId}
     , AMD_DT              =         SYSDATE
     , MIME_TYPE_NM        =         #{mimeTypeNm}

mapper.delete:
DELETE FROM TB_SYS_FILE_BAS
 WHERE FILE_CD            =       TO_CHAR(#{fileCd})

   AND FILE_ODRG           =       #{fileOdrg}
