mapper.select:
SELECT COUNT(1) CNT FROM TB_SYS_GRP_BAS A , TB_SYS_GRP_USER_REL B , TB_SYS_USER_BAS C WHERE A.GRP_ID = B.GRP_ID AND B.USER_ID = C.USER_ID
AND A.GRP_ID != #{exGroupCd}
AND A.GRP_ID = #{grpId}
AND C.DEL_YN = 'N'

SELECT A.GRP_ID
     , A.GRP_NM
     , DECODE( NVL(B.GRP_ID,C.GRP_ID),NULL,'N','Y') CHECK_YN
  FROM TB_SYS_GRP_BAS A
  LEFT JOIN TB_SYS_GRP_USER_REL B ON A.GRP_ID = B.GRP_ID AND B.USER_ID = #{userId}
  LEFT JOIN TB_SYS_GRP_MENU_REL C ON A.GRP_ID = C.GRP_ID AND C.MENU_ID = #{menuId}

SELECT A.GRP_ID , C.USER_ID , C.LOGIN_ID , C.USER_NM , C.EMAIL_ADDR , C.MPHON_NO FROM TB_SYS_GRP_BAS A , TB_SYS_GRP_USER_REL B , TB_SYS_USER_BAS C WHERE A.GRP_ID = B.GRP_ID AND B.USER_ID = C.USER_ID
AND A.GRP_ID != #{exGroupCd}
AND A.GRP_ID = #{grpId}
AND C.DEL_YN = 'N' ORDER BY C.LOGIN_ID

SELECT COUNT(1) CNT FROM TB_SYS_GRP_BAS A , TB_SYS_GRP_MENU_REL B , TB_SYS_MENU_BAS C WHERE A.GRP_ID = B.GRP_ID AND B.MENU_ID = C.MENU_ID
AND A.GRP_ID != #{exGroupCd}
AND A.GRP_ID = #{grpId}
AND C.TOP_MENU_ID = #{topMenuId}
AND C.MENU_URL IS NOT NULL AND C.MENU_LVL_NO > 1 AND C.MENU_AUTH_YN = 'Y'

SELECT A.GRP_ID , C.MENU_ID , C.MENU_NM , C.MENU_URL , C.TOP_MENU_ID , B.MENU_REG_AUTH_YN , B.MENU_READ_AUTH_YN , B.MENU_AMD_AUTH_YN , B.MENU_DEL_AUTH_YN , ( SELECT LISTAGG(MENU_NM, '>') WITHIN GROUP(ORDER BY MENU_LVL_NO) FROM TB_SYS_MENU_BAS START WITH MENU_ID = C.MENU_ID CONNECT BY PRIOR PRNTS_MENU_ID = MENU_ID ) MENU_PATH FROM TB_SYS_GRP_BAS A , TB_SYS_GRP_MENU_REL B , TB_SYS_MENU_BAS C WHERE A.GRP_ID = B.GRP_ID AND B.MENU_ID = C.MENU_ID
AND A.GRP_ID != #{exGroupCd}
AND A.GRP_ID = #{grpId}
AND C.TOP_MENU_ID = #{topMenuId}
AND C.MENU_URL IS NOT NULL AND C.MENU_LVL_NO > 1 AND C.MENU_AUTH_YN = 'Y' ORDER BY C.MENU_ID
